    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        Consumer consumer = new Consumer(jTextField1.getText(), jTextField2.getText());
        consumer.Start();
        jTextField1.setText("");
        jTextField2.setText("");
        jTextArea1.setText("");
        jTextArea1.setText(consumer.getSearchResult());
    }//GEN-LAST:event_jButton1ActionPerformed

